CREATE  TABLE public.lesson_songs (
  lesson_id uuid NOT NULL,
  song_id uuid NOT NULL,
  created_at timestamp with time zone NOT NULL DEFAULT now(),
  updated_at timestamp with time zone NOT NULL DEFAULT now(),
  song_status public.LearningSongStatus NULL DEFAULT 'STARTED'::"LearningSongStatus",
  CONSTRAINT fk_lesson FOREIGN KEY (lesson_id) REFERENCES lessons(id) ON DELETE CASCADE,
  CONSTRAINT fk_song FOREIGN KEY (song_id) REFERENCES songs(id) ON DELETE CASCADE
) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_lesson_songs_lesson_id ON public.lesson_songs USING btree (lesson_id) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_lesson_songs_song_id ON public.lesson_songs USING btree (song_id) TABLESPACE pg_default;

CREATE  TABLE public.user_favorites (
  user_id uuid NOT NULL,
  song_id uuid NOT NULL,
  created_at timestamp with time zone NOT NULL DEFAULT now(),
  id uuid NOT NULL DEFAULT gen_random_uuid(),
  CONSTRAINT user_favorites_pkey PRIMARY KEY (id),
  CONSTRAINT fk_song FOREIGN KEY (song_id) REFERENCES songs(id) ON DELETE CASCADE,
  CONSTRAINT fk_user FOREIGN KEY (user_id) REFERENCES auth.users(id) ON DELETE CASCADE
) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_user_favorites_user_id ON public.user_favorites USING btree (user_id) TABLESPACE pg_default;
CREATE INDEX IF NOT EXISTS idx_user_favorites_song_id ON public.user_favorites USING btree (song_id) TABLESPACE pg_default;
